import React, { useEffect, useState } from "react";

import "react-quill/dist/quill.snow.css";
import './Social.css';
import bg from './bg2.jpg';

const ipfsUri480 = "https://croskull.mypinata.cloud/ipfs/QmWu9bKunKbv8Kkq8wEWGpCaW47oMBbH6ep4ZWBzAxHtgj/";
const ipfsUri128 = "https://croskull.mypinata.cloud/ipfs/QmZn1HvYE1o1J8LhNpxFTj5k8LQb2bWT49YvbrhB3r19Xx/";


const Profile = ({ accountAddress }) => {

  const openbar = () =>
  {
    document.getElementById("sidebar").style.width = "250px";
  }
  const closebar = () =>
  {
    document.getElementById("sidebar").style.width = "0px";
  }
  return (

    <>
      <div className="profile-container">
        <div className="profile-content">

          <div className="profile-header">
            <div className="header-banner" style={{ backgroundImage: 'url(' + bg + ')' }}>
              <button className="skull-button" onClick={()=>openbar()}>+</button>
              <button className="skull-button" onClick={()=>closebar()}>-</button>
            </div>
            <div className="header-navbar" >
              <img src={ipfsUri128 + '27.webp'} className="pro-pic"/>
              <div className="navbar-button">
                <p>Home</p>
                <p>About</p>
                <p>Follower</p>
                <p>Following</p>
              </div>
            </div>
          </div>

        </div>
        <div className="profile-bar-h" id="sidebar">
          <p>Stories</p>
          <p>Follow</p>
        </div>
      </div>

    </>
  )
}


export default Profile;
