import React, { useEffect, useState } from "react";

import "react-quill/dist/quill.snow.css";
import coins from "./coins.png";
import './Bank.css';
import Grave from "../Navbar/grave.png";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHourglassHalf, faQuestionCircle } from '@fortawesome/free-solid-svg-icons';
import MetricContainer from "../MetricContainer/MetricContainer";
import Soul from "../Navbar/soul.png";
import Rune from "../Navbar/grave-burn.png";
import Coins from "./coins.png";
const ipfsUri480 = "https://croskull.mypinata.cloud/ipfs/QmWu9bKunKbv8Kkq8wEWGpCaW47oMBbH6ep4ZWBzAxHtgj/";
const ipfsUri128 = "https://croskull.mypinata.cloud/ipfs/QmZn1HvYE1o1J8LhNpxFTj5k8LQb2bWT49YvbrhB3r19Xx/";

const Bank = ({ accountAddress }) => {
  const [detailsView, setDetailsView] = useState(false)

  const handleFieldChange = ( event ) => {
    let value = event.target ? event.target.value.replace(/</g, "&lt;").replace(/>/g, "&gt;") : event
    let name = event.target ? event.target.id : "description"
    let type = event.target ? event.target.type : "description"
    console.log( value, name, type )
    if( type === 'date' ){
      value = parseInt( new Date( value ).getTime() / 1000 )
    }
  }

  return (

    <>
      <div className="sk-container" style={{ overflow: 'hidden' }}>
        <div className="sk-row sk-flex gd-container">
          <div className="sk-row-bank">
            <div className="sk-box-bank">
              <p>$456.565.667 Staked</p>
              <p>Total Value Locked</p>
            </div>
            <div className="sk-box-bank ">
              <p>$456.565.667 Staked</p>
              <p>Total Value Locked</p>
            </div>
          </div>
          <div className="sk-row-bank">
            <div className="sk-box-bank ">
              <p>$456.565.667 Staked</p>
              <p>Total Value Locked</p>
            </div>
            <div className="sk-box-bank">
              <p>$456.565.667 Staked</p>
              <p>Total Value Locked</p>
            </div>
          </div>
        </div>
        <div class="switcher-container">
                <div className=" switcher-wrapper">
                    <button
                      className={`switcher-button view-button ${ detailsView ? '' : 'active'}`}
                      onClick={
                        () => setDetailsView(false)
                      }
                    >
                      Bank
                    </button>
                    <button
                      className={`switcher-button view-button ${ ! detailsView ? '' : 'active'}`}
                      onClick={
                        () => setDetailsView(true)
                      }
                    >
                      Vault
                    </button>
                </div>
                <div className='sk-box-content box-1' hidden={detailsView}>
                  <div className="sk-box bank-box">
                    <h1>Earn $Rude</h1>
                    <hr></hr>
                    <div className="bank-content">
                      <p>APY/APR<span>7%</span></p>
                      <p>Staked<span>7</span></p>
                      <p>Profit<span>3,5</span></p>
                      <p className="balance"> 0.0</p>
                      <div className="input-content">
                        <button className="skull-button">MAX</button>
                        <input id="input-grave" type="number" placeholder="0"></input>
                      </div>
                      <button className="skull-button stake-button">STAKE</button>
                      <button className="skull-button claim-button">CLAIM</button>
                      <button className="skull-button unstake-button">UNSTAKE {"&"} CLAIM</button>
                    </div>
                    </div>
                </div>
                <div className='sk-box-content box-2' hidden={!detailsView} >
                  box 2
                </div>
              </div>
      </div>
    </>
  )
}


export default Bank;
