import { ethers } from "ethers";
import store from "../store";





const updateState = (payload) => {
    return {
        type: "UPDATE_STATE",
        payload: payload
    }
}

export const loadAllSkull = (page) => {
    return async (dispatch) => {
        let { gallery } = store.getState()
        const rawResult = await fetch( 'https://croskull.mypinata.cloud/ipfs/QmSrjCsmQ9e5m1HFYXRSYgxHi9K6u9a6DXRsWz7KWW5i6p/_metadata' );
        let rawList = await rawResult.json();
        let {skullsList} = gallery;
        if(!skullsList)
        skullsList = []
        for(let i=page*100; i<page*100+100; i++)
        {
            skullsList.push(rawList[i]);
        }
        dispatch(updateState({
            key: "skullsList",
            value: skullsList,
        }))
    }
}

export const loadFilterSkull = (filter,page) => {
    return async (dispatch) => {
        let { gallery } = store.getState()
        const rawResult = await fetch( 'https://croskull.mypinata.cloud/ipfs/QmSrjCsmQ9e5m1HFYXRSYgxHi9K6u9a6DXRsWz7KWW5i6p/_metadata' );
        let rawList = await rawResult.json();
        let start=page*100;
        let skullsList = []
        console.log(start,start+100, start>(start+100))
        rawList.map( (skull,i) =>{
            if(start>(start+100))
            {
                console.log('dentro')
                return;
            }
                
                else{
                    if(checkFilter(skull,filter))
                    {
                        skullsList.push(skull);
                        start++;
                        console.log(start);
                    }
                }
            console.log(start>(start+100))
        })
        if(skullsList.length ==0)
            skullsList = null;
        dispatch(updateState({
            key: "skullsList",
            value: skullsList,
        }))
    }
}
function checkFilter(cr,filter) {
    let flag = true;
    filter.map(f => {
        if (f.value.length > 0 && flag) {
            cr.attributes.map(at => {
                if (at.trait_type == f.name && flag) {
                    if (f.value.includes(at.value))
                        flag = true
                    else {
                        flag = false;
                    }
                }
            })

        }
    })
    return flag;
}
export const addPage = (page) =>{
    return async (dispatch) => {
        let { gallery } = store.getState()
        if(page)
        console.log('')
        else
        page=-1
        let p = page+1;
        dispatch(updateState({
            key: "page",
            value: p,
        }))
    }
}


export const resetSkullList = () => {
    return async (dispatch) => {
        let { gallery } = store.getState()
        let skullsList = null;

        dispatch(updateState({
            key: "skullsList",
            value: skullsList
        }))
    }
}

export const loadSkull= (i) => {
    return async (dispatch) => {
        let { gallery } = store.getState()
        const rawResult = await fetch( 'https://croskull.mypinata.cloud/ipfs/QmSrjCsmQ9e5m1HFYXRSYgxHi9K6u9a6DXRsWz7KWW5i6p/_metadata' );
        let skullsList = await rawResult.json();
        let skull;
        skullsList.map(s =>{
            if(s.edition = i)
                skull=s;
        })
        dispatch(updateState({
            key: "skullsList",
            value: skullsList
        }))
    }
}

export const getEbisusLink= (skull) => {
    return async (dispatch) => {
        let { gallery } = store.getState()
        const rawResult = await (await fetch(`https://api.ebisusbay.com/listings?collection=0xF87A517A5CaecaA03d7cCa770789BdB61e09e05F&state=0&page=1&pageSize=6666`)).json();
        let skullsList = await rawResult.listings;
        const link = 'https://app.ebisusbay.com/listing/';
        let linkGenerate = null;
        
        console.log(skull.edition);
        skullsList.map(s =>{
            console.log(s.nftId == skull.edition);
            if(s.nftId == skull.edition )
                linkGenerate = link+s.listingId
        })
        dispatch(updateState({
            key: "ebisusLink",
            value: linkGenerate
        }))
    }
}

export const getAttribute = () =>{
    return async (dispatch) => {
    const rawResult = await fetch( 'https://croskull.mypinata.cloud/ipfs/QmSrjCsmQ9e5m1HFYXRSYgxHi9K6u9a6DXRsWz7KWW5i6p/_metadata' );
    let skullsList = await rawResult.json();
    let attributeList = [
        {name:'Background',value:[]},
        {name:'Skull',value:[]},
        {name:'Body',value:[]},
        {name:'Nose',value:[]},
        {name:'Eyes',value:[]},
        {name:'Hat',value:[]},
        {name:'Trait',value:[5,6]}
        ];
    skullsList.map(skull =>{
        (skull.attributes).map( at =>{
            switch (at.trait_type) {
                case 'Background':
                    if (!attributeList[0].value.includes(at.value))
                        attributeList[0].value.push(at.value);
                    break;
                case 'Skull':
                    if (!attributeList[1].value.includes(at.value))
                        attributeList[1].value.push(at.value);
                    break;
                case 'Body':
                    if (!attributeList[2].value.includes(at.value))
                        attributeList[2].value.push(at.value);
                    break;
                case 'Nose':
                    if (!attributeList[3].value.includes(at.value))
                        attributeList[3].value.push(at.value);
                    break;
                case 'Eyes':
                    if (!attributeList[4].value.includes(at.value))
                        attributeList[4].value.push(at.value);
                    break;
                case 'Hat':
                    if (!attributeList[5].value.includes(at.value))
                        attributeList[5].value.push(at.value);
                    break;
                default:
                    break;
            }
        })
    })
    dispatch(updateState({
        key: "attributesList",
        value: attributeList
    }))
    }
  }
