'use strict';

var convert = require('./convert');

var protocols = require('./protocols-table');

var varint = require('varint');

var uint8ArrayConcat = require('uint8arrays/concat');

var uint8ArrayToString = require('uint8arrays/to-string'); // export codec


module.exports = {
  stringToStringTuples: stringToStringTuples,
  stringTuplesToString: stringTuplesToString,
  tuplesToStringTuples: tuplesToStringTuples,
  stringTuplesToTuples: stringTuplesToTuples,
  bytesToTuples: bytesToTuples,
  tuplesToBytes: tuplesToBytes,
  bytesToString: bytesToString,
  stringToBytes: stringToBytes,
  fromString: fromString,
  fromBytes: fromBytes,
  validateBytes: validateBytes,
  isValidBytes: isValidBytes,
  cleanPath: cleanPath,
  ParseError: ParseError,
  protoFromTuple: protoFromTuple,
  sizeForAddr: sizeForAddr
}; // string -> [[str name, str addr]... ]

function stringToStringTuples(str) {
  var tuples = [];
  var parts = str.split('/').slice(1); // skip first empty elem

  if (parts.length === 1 && parts[0] === '') {
    return [];
  }

  for (var p = 0; p < parts.length; p++) {
    var part = parts[p];
    var proto = protocols(part);

    if (proto.size === 0) {
      tuples.push([part]);
      continue;
    }

    p++; // advance addr part

    if (p >= parts.length) {
      throw ParseError('invalid address: ' + str);
    } // if it's a path proto, take the rest


    if (proto.path) {
      tuples.push([part, // TODO: should we need to check each path part to see if it's a proto?
      // This would allow for other protocols to be added after a unix path,
      // however it would have issues if the path had a protocol name in the path
      cleanPath(parts.slice(p).join('/'))]);
      break;
    }

    tuples.push([part, parts[p]]);
  }

  return tuples;
} // [[str name, str addr]... ] -> string


function stringTuplesToString(tuples) {
  var parts = [];
  tuples.map(function (tup) {
    var proto = protoFromTuple(tup);
    parts.push(proto.name);

    if (tup.length > 1) {
      parts.push(tup[1]);
    }
  });
  return cleanPath(parts.join('/'));
} // [[str name, str addr]... ] -> [[int code, Uint8Array]... ]


function stringTuplesToTuples(tuples) {
  return tuples.map(function (tup) {
    if (!Array.isArray(tup)) {
      tup = [tup];
    }

    var proto = protoFromTuple(tup);

    if (tup.length > 1) {
      return [proto.code, convert.toBytes(proto.code, tup[1])];
    }

    return [proto.code];
  });
} // [[int code, Uint8Array]... ] -> [[str name, str addr]... ]


function tuplesToStringTuples(tuples) {
  return tuples.map(function (tup) {
    var proto = protoFromTuple(tup);

    if (tup.length > 1) {
      return [proto.code, convert.toString(proto.code, tup[1])];
    }

    return [proto.code];
  });
} // [[int code, Uint8Array ]... ] -> Uint8Array


function tuplesToBytes(tuples) {
  return fromBytes(uint8ArrayConcat(tuples.map(function (tup) {
    var proto = protoFromTuple(tup);
    var buf = Uint8Array.from(varint.encode(proto.code));

    if (tup.length > 1) {
      buf = uint8ArrayConcat([buf, tup[1]]); // add address buffer
    }

    return buf;
  })));
}

function sizeForAddr(p, addr) {
  if (p.size > 0) {
    return p.size / 8;
  } else if (p.size === 0) {
    return 0;
  } else {
    var size = varint.decode(addr);
    return size + varint.decode.bytes;
  }
} // Uint8Array -> [[int code, Uint8Array ]... ]


function bytesToTuples(buf) {
  var tuples = [];
  var i = 0;

  while (i < buf.length) {
    var code = varint.decode(buf, i);
    var n = varint.decode.bytes;
    var p = protocols(code);
    var size = sizeForAddr(p, buf.slice(i + n));

    if (size === 0) {
      tuples.push([code]);
      i += n;
      continue;
    }

    var addr = buf.slice(i + n, i + n + size);
    i += size + n;

    if (i > buf.length) {
      // did not end _exactly_ at buffer.length
      throw ParseError('Invalid address Uint8Array: ' + uint8ArrayToString(buf, 'base16'));
    } // ok, tuple seems good.


    tuples.push([code, addr]);
  }

  return tuples;
} // Uint8Array -> String


function bytesToString(buf) {
  var a = bytesToTuples(buf);
  var b = tuplesToStringTuples(a);
  return stringTuplesToString(b);
} // String -> Uint8Array


function stringToBytes(str) {
  str = cleanPath(str);
  var a = stringToStringTuples(str);
  var b = stringTuplesToTuples(a);
  return tuplesToBytes(b);
} // String -> Uint8Array


function fromString(str) {
  return stringToBytes(str);
} // Uint8Array -> Uint8Array


function fromBytes(buf) {
  var err = validateBytes(buf);
  if (err) throw err;
  return Uint8Array.from(buf); // copy
}

function validateBytes(buf) {
  try {
    bytesToTuples(buf); // try to parse. will throw if breaks
  } catch (err) {
    return err;
  }
}

function isValidBytes(buf) {
  return validateBytes(buf) === undefined;
}

function cleanPath(str) {
  return '/' + str.trim().split('/').filter(function (a) {
    return a;
  }).join('/');
}

function ParseError(str) {
  return new Error('Error parsing address: ' + str);
}

function protoFromTuple(tup) {
  var proto = protocols(tup[0]);
  return proto;
}
