import React, { useEffect, useState } from "react";

import "react-quill/dist/quill.snow.css";
import coins from "./coins.png";
import './Raffle.css';
import Grave from "../Navbar/grave.png";
import MetricContainer from "../MetricContainer/MetricContainer";
const ipfsUri480 = "https://croskull.mypinata.cloud/ipfs/QmWu9bKunKbv8Kkq8wEWGpCaW47oMBbH6ep4ZWBzAxHtgj/"
const ipfsUri128 = "https://croskull.mypinata.cloud/ipfs/QmZn1HvYE1o1J8LhNpxFTj5k8LQb2bWT49YvbrhB3r19Xx/"
const Raffle = ({accountAddress}) => {
  const raf = {
    type: "Normal-Raffle",
    title: "First Raffle",
    nWinners: 5,
    nPart: 10,
    cost: 5,
    start: '12/03/2022',
    finish: '25/03/2022',
    partecipants: [],
    winners: []

  }
  const formatDate = ( timestamp ) => {
    let tsHours = timestamp / 60 / 60
    let days =  parseInt( timestamp / DAY_IN_SEC )
    let hoursDiff = tsHours - ( days * 24 )
    let hours = parseInt(hoursDiff)
    let tsMinutes = hoursDiff * 60
    let minutes = parseInt( tsMinutes - ( hours * 60 ) )
    return {
      days,
      hours,
      minutes
    }
  }
  
  let raffleDurationDate = formatDate( raf.finish - raf.start );
  return (
    
    <>
    <div className="sk-container sk-row">
      <div className="sk-box sk-column">
      <h3>Raffle Time!</h3>
      <p>
      Lorem ipsum dolor sit amet, consectetur adipiscing elit. Ut eget urna ex. Etiam eleifend interdum lobortis. Morbi laoreet purus sed felis semper posuere.
      </p>
        <div className="sk-box-content sk-raffle-item sk-row sk-flex">
          <div className="wd-66">
          <img src={coins} style={{maxWidth: '15%', float: 'left'}} />
          <div style={{marginLeft:'18%'}}>
            <h5>{raf.type} | N-Winners: {raf.nWinners} | N-Partecipants: {raf.nPart}</h5>
            <h3>{raf.title}</h3>
            <hr></hr>
            <p>
              Join now this amazing raffle in partnership with MM.Finance. X<img src={Grave} className="skull-icon" /> will be airdropped!
            </p>
            </div>
          </div>
          <div className="wd-33" style={{textAlign: 'center'}}>
              <div style={{textAlign: 'center'}}>
                Cost: 5<img src={Grave} className="skull-icon" /> - {}
              </div>
              <button className="skull-button">Partecipate</button>
            </div>
        </div>
        <div className="sk-box-content sk-raffle-item">
          
          </div>
      </div>

    </div>
     </>
  )
}


export default Raffle;
