import React, { useEffect, useState } from "react";

import "react-quill/dist/quill.snow.css";
import coins from "./coins.png";
import './Raffle.css';
import Grave from "../Navbar/grave.png";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHourglassHalf } from '@fortawesome/free-solid-svg-icons';
import MetricContainer from "../MetricContainer/MetricContainer";
const ipfsUri480 = "https://croskull.mypinata.cloud/ipfs/QmWu9bKunKbv8Kkq8wEWGpCaW47oMBbH6ep4ZWBzAxHtgj/"
const ipfsUri128 = "https://croskull.mypinata.cloud/ipfs/QmZn1HvYE1o1J8LhNpxFTj5k8LQb2bWT49YvbrhB3r19Xx/"
const Raffle = ({ accountAddress }) => {
  const raf2 = {
    type: "Normal-Raffle",
    title: "Second Raffle",
    nWinners: 5,
    nPart: 10,
    cost: 5,
    start: new Date(2022, 2, 12),
    finish: new Date(2022, 2, 25),
    partecipants: [],
    winners: []
  }
  const raf1 = {
    type: "Crazy-Raffle",
    title: "First Raffle",
    nWinners: 5,
    nPart: 10,
    cost: 5,
    start: new Date(2022, 1, 12),
    finish: new Date(2022, 1, 25),
    partecipants: [],
    winners: []
  }

  const rafs = [raf1, raf2, raf1, raf2, raf1, raf2, raf1, raf2, raf1, raf2];

  const DAY_IN_SEC = 60 * 60 * 24 * 1000;
  const HUNDRED_DAYS_IN_SEC = 100 * DAY_IN_SEC//100 * DAY_IN_SEC;
  const formatDate = (timestamp) => {
    console.log(timestamp);
    let tsHours = timestamp / 60 / 60 / 1000
    let days = parseInt(timestamp / DAY_IN_SEC)
    let hoursDiff = tsHours - (days * 24)
    let hours = parseInt(hoursDiff)
    let tsMinutes = hoursDiff * 60
    let minutes = parseInt(tsMinutes - (hours * 60))
    return {
      days,
      hours,
      minutes
    }
  }


  return (

    <>
      <div className="sk-container sk-row" style={{ overflow: 'hidden' }}>
        <div className="sk-box sk-column sk-raffle-item">
          <h3>Raffle Time!</h3>
          <p>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Ut eget urna ex. Etiam eleifend interdum lobortis. Morbi laoreet purus sed felis semper posuere.
          </p>
          {(rafs.map(raf => {
            let raffleDuration = raf.finish - new Date();
            let raffleDurationDate = formatDate(raffleDuration);
            return (
              <div className="sk-box-content sk-row sk-flex">
                <div className="raffle-box wd-66">

                  <div >
                    <h5>{raf.type} | N-Winners: {raf.nWinners} | N-Partecipants: {raf.nPart}</h5>
                    <h3>{raf.title}</h3>
                    <hr></hr>
                    <p>
                      Join now this amazing raffle in partnership with MM.Finance. X<img src={Grave} className="skull-icon" /> will be airdropped!
                    </p>
                  </div>
                </div>
                {
                  raffleDuration > 0 ?
                    <div className="raffle-box wd-33" style={{ textAlign: 'center', position: 'relative' }}>

                      Cost: 5<img src={Grave} className="skull-icon" />
                      <br></br>
                      <br></br>
                      Ending in: {raffleDurationDate.days}D {raffleDurationDate.hours}H {raffleDurationDate.minutes}M <FontAwesomeIcon icon={faHourglassHalf} />
                      <br />
                      <button className="skull-button" style={{ marginTop: '13%', width: '80%' }}>Partecipate</button>
                    </div>
                    :
                    <div className="raffle-box wd-33" style={{ textAlign: 'center', position: 'relative' }}>

                      Started: {}
                      <br></br>
                      <br></br>
                      Ended: {}
                      <br />
                      <button className="skull-button" style={{ marginTop: '13%', width: '80%' }}>View Winners</button>
                    </div>
                }
              </div>
            )
          }))}


        </div>

      </div>
    </>
  )
}


export default Raffle;
