import React,{useEffect } from "react";
import './AccountDetails.css';
import banner from "./banner.png"
import { ethers } from 'ethers';
import store from "../../redux/store";


const AccountDetails = () => {
  let { blockchain } = store.getState();
  let { accountBalance, accountAddress, contractDetected } = blockchain

  useEffect(() => {   
    window.addEventListener("scroll", listenToScroll);
    return () => 
       window.removeEventListener("scroll", listenToScroll); 
  }, [])

  const listenToScroll = () => {
    let cr = document.getElementById('croskull-div').offsetTop;
    let tavern = document.getElementById('tavern-div').offsetTop;
    let adventure = document.getElementById('adventure-div').offsetTop;
    let winScroll = document.body.scrollTop || 
    document.documentElement.scrollTop;

    if((winScroll - cr)<100 && (winScroll - cr)>0)
    showCroskullDiv()

    console.log(cr,tavern,adventure,winScroll);
  };

  function showCroskullDiv(){
    let d1 = document.getElementById('tavern-div').getElementsByClassName('div-img');
    let d2 = document.getElementById('tavern-div').getElementsByClassName('div-text');
    console.log(d1[0].style.width);

  }
  if( contractDetected ){
    accountBalance = formatEther(accountBalance).slice(0, 5 )
  }
  return (
    <>
      <div className="container ac-home">
        <h1 className="ac-title">CroSkull NFT</h1>
        <img 
          src={banner} 
          width={"100%"}
          alt="CroSkulls Banner"
        />
        <p className="ac-text ac-br">
          The Croskull is a collection of 6,666 uniquely generated NFTs stored in the Cronos Chain.
        <br></br>
          Each Croskull NFT is based on 6+ attributes with different rarities.
        </p>
        <p className="ac-text ac-br">
        Want to know more? <span><a className="link" href="https://www.croskull.com">Click Here!</a></span>
        </p>
        <hr className="my-4" />
        <p className="ac-text">Account address :</p>
        <h4 className="ac-text">{accountAddress}</h4>
        <p className="ac-text">Account balance :</p>
        <h4 className="ac-text">{accountBalance} CRO</h4>
      </div>
      <div className="container container-prova">
        <div id="croskull-div">
        <p className="ac-text ac-br">
          The Croskull is a collection of 6,666 uniquely generated NFTs stored in the Cronos Chain.
        <br></br>
          Each Croskull NFT is based on 6+ attributes with different rarities.
        </p>
        </div>
        <div id="tavern-div" className="sk-row sk-flex">
          <div className="div-img"  >
          <img src={banner} 
          width={"100%"}
          alt="CroSkulls Banner"></img>
          </div>
          <div className="div-text"  >
          <p>PARLO DELLA TAVERNA</p>
          <p>PARLO DELLE STORIES</p>
          <p>PARLO DELL'INVENTARIO</p>
          </div>
        </div>
        <div id="adventure-div">
          <p>PARLO DELL'avventura</p>
          <p>PARLO DEL BURN</p>
          <p>PARLO DELLE SOULS</p>
        </div>
      </div>
      </>
  );
};

const formatEther = num => ethers.utils.formatEther(num)

export default AccountDetails;
