import React, { useEffect, useState } from "react";

import "react-quill/dist/quill.snow.css";
import coins from "./coins.png";
import './Bank.css';
import Grave from "../Navbar/grave.png";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHourglassHalf, faQuestionCircle } from '@fortawesome/free-solid-svg-icons';
import MetricContainer from "../MetricContainer/MetricContainer";
import Soul from "../Navbar/soul.png";
import Rune from "../Navbar/grave-burn.png";
import Coins from "./coins.png";
const ipfsUri480 = "https://croskull.mypinata.cloud/ipfs/QmWu9bKunKbv8Kkq8wEWGpCaW47oMBbH6ep4ZWBzAxHtgj/";
const ipfsUri128 = "https://croskull.mypinata.cloud/ipfs/QmZn1HvYE1o1J8LhNpxFTj5k8LQb2bWT49YvbrhB3r19Xx/";

const Bank = ({ accountAddress }) => {
  const [detailsView, setDetailsView] = useState(false)

  return (

    <>
      <div className="sk-container" style={{ overflow: 'hidden' }}>
        <div className="sk-row sk-flex gd-container">
          <div className="sk-box-bank">
            <p>$456.565.667 Staked</p>
            <p>Total Value Locked</p>
          </div>
          <div className="sk-box-bank ">
            <p>$456.565.667 Staked</p>
            <p>Total Value Locked</p>
          </div>
          <div className="sk-box-bank ">
            <p>$456.565.667 Staked</p>
            <p>Total Value Locked</p>
          </div>
          <div className="sk-box-bank">
            <p>$456.565.667 Staked</p>
            <p>Total Value Locked</p>
          </div>
        </div>
        <div class="switcher-container">
                <div className=" switcher-wrapper">
                    <button
                      className={`switcher-button view-button ${ detailsView ? '' : 'active'}`}
                      onClick={
                        () => setDetailsView(false)
                      }
                    >
                      Bank
                    </button>
                    <button
                      className={`switcher-button view-button ${ ! detailsView ? '' : 'active'}`}
                      onClick={
                        () => setDetailsView(true)
                      }
                    >
                      Vault
                    </button>
                </div>
                <div className={`sk-box-content sk-column first ${ ! detailsView ? 'show' : 'hide'}`}>
                  box 1
                </div>
                <div className='sk-box-content sk-column' hidden={detailsView} >
                  box 2
                </div>
              </div>
      </div>
    </>
  )
}


export default Bank;
